<!--*****************************************************************************************画面名：ログインチェック機能概要：掲示板を利用するアカウントのログインチェックをする        １.アカウント名とパスワードでAND検索でレコードチェック        ２.ログインボタン押下後、カテゴリ選択画面に遷移する        ３.*****************************************************************************************--><?phprequire_once("../model/DbPdo.php");?><!DOCTYPE html><html>    <head>        <meta charset="utf-8">        <title></title>    </head>    <body>        <?php        // ログイン画面から変数を取得        $mailAddress = filter_input(INPUT_POST, "mailAddress");        $password = filter_input(INPUT_POST, "password");        // エラーフラグ        $err_flg = false;        // レコード数        $cnt = 0;        // 変数の入力チェック        if ($mailAddress == "") {            echo "アカウントが入力されていません。<br />";            $err_flg = true;        }        if ($password == "") {            echo "パスワードが入力されていません。<br />";            $err_flg = true;        }        if (!$err_flg) {            // アカウント情報の重複チェック            $sql = "SELECT * FROM login_users WHERE address = "."'".$mailAddress."'"." AND password = "."'".$password."'"." AND del_flg = 0;";            $result_cnt = DbPdo::CountPdo($sql);            if ($result_cnt == 0) {                exit('ログインできません。<br />アカウントとパスワードをもう一度確認してください。<br />');            } else {                // ユーザIDを取得する                $result = DbPdo::SelectPdo($sql);                ?>                <!-- ログイン処理成功時、カテゴリ表示画面にPOSTでアカウント情報を保持して遷移する -->                <form id="loginCheckForm" name="loginCheckForm" action="../view/dispCategories.php" method="POST">                    <input type="hidden" name="userID" value="<?php echo $result['id']; ?>">                    <input type="hidden" name="userName" value="<?php echo $dispArray['name']; ?>">                    <input type="submit" value="カテゴリ表示画面へ">                </form>                <!-- $_SESSION['login'] = "OK";                $_SESSION['mailAddress'] = $mailAddress;                $_SESSION['password'] = $password;            <?php } ?>        <?php } ?></body></html>