<!--*****************************************************************************************画面名：ログインチェック機能概要：掲示板を利用するアカウントのログインチェックをする        １.アカウント名とパスワードでAND検索でレコードチェック        ２.ログインボタン押下後、カテゴリ選択画面に遷移する        ３.*****************************************************************************************--><?php// ログインモデルクラス読み込みrequire_once("../model/login_user.php");// ログイン画面から変数を取得$mailAddress = filter_input(INPUT_POST, "mailAddress");$password = filter_input(INPUT_POST, "password");// アカウント情報の重複チェックif (login_user::ChkUniqAccount($mailAddress, $password) == "OK") {// ユーザID取得    $userID = login_user::GetUserID($mailAddress, $password);// ログイン処理成功時、カテゴリ表示画面にPOSTでアカウント情報を保持して遷移する    echo <<< EOM<html><head><title></title></head><body><!-- ログイン処理成功時、カテゴリ表示画面にPOSTでアカウント情報を保持して遷移する --><form id="loginCheckForm" name="loginCheckForm" action="../view/dispCategories.php" method="POST"><input type="hidden" name="userID" value="$userID"><input type="hidden" name="action" value="login"><input type="submit" value="カテゴリ表示画面へ"></form></body></html>EOM;}