<!--*****************************************************************************************画面名：ログインチェック機能概要：掲示板を利用するアカウントのログインチェックをする        １.アカウント名とパスワードでAND検索でレコードチェック        ２.ログインボタン押下後、カテゴリ選択画面に遷移する        ３.*****************************************************************************************--><?phpsession_start();// ログインモデルクラス読み込みrequire_once("../model/login_user.php");// ログイン画面から変数を取得$mailAddress = filter_input(INPUT_POST, "mailAddress");$password = filter_input(INPUT_POST, "password");// アカウント情報の重複チェックif (login_user::ChkUniqAccount($mailAddress, $password) == "OK") {// ユーザID取得    $userID = login_user::GetUserID($mailAddress, $password);// ユーザIDをセッションに格納    $_SESSION['userID'] = $userID;// ログイン処理成功後、画面切替画面に遷移    echo <<< EOM<html><head><title></title></head><body><form name="loginForm" action="./changeDisp.php" method="POST"><input type="hidden" name="action" value="login"><script language="JavaScript">document.loginForm.submit();</script></form></body></html>EOM;}