<!--*****************************************************************************************画面名：ログインチェック機能概要：掲示板を利用するアカウントのログインチェックをする	１.アカウント名とパスワードでAND検索でレコードチェック	２.ログインボタン押下後、カテゴリ選択画面に遷移する	３.*****************************************************************************************--><!DOCTYPE html><html><head>	<title></title></head><body>	<?php	// ログイン画面から変数を取得	$mailAddress = $_POST['mailAddress'];	$password = $_POST['password'];	// エラーフラグ	$err_flg = false;	// レコード数	$cnt = 0;	// 変数の入力チェック	if ($mailAddress == "") {		echo "アカウントが入力されていません。<br />";		$err_flg = true;	}	if ($password == "") {		echo "パスワードが入力されていません。<br />";		$err_flg = true;	}	if (!$err_flg) {		// サーバ接続		$link = mysql_connect('localhost', 'root', 'root');		if (!$link) {		    die('接続失敗です。'.mysql_error());		}		// DB選択		$db_selected = mysql_select_db('BulltinBoardSystem', $link);		if (!$db_selected){		    die('DB選択失敗です。'.mysql_error());		}		// MySQLに対する処理		mysql_set_charset('utf8');		// アカウント情報の重複チェック		$sql_check = "SELECT COUNT(*) AS cnt FROM login_users WHERE address = '$mailAddress' AND password = '$password' AND del_flg = 0;";		$result_check = mysql_query($sql_check);		$row = mysql_fetch_assoc($result_check);		$cnt = $row['cnt'];		if ($cnt == 0) {			exit('ログインできません。<br />アカウントとパスワードをもう一度確認してください。<br />');		} else {		// ユーザIDを取得する			$sql = "SELECT * FROM login_users WHERE address = '$mailAddress' AND del_flg = 0";			$result = mysql_query($sql);			if (!$result) {				exit('データを取得できませんでした。');			}			// DBから画面に表示する値を取得し、配列に入れる			$dispArray[] = "";			$cntArray = 0;			while ($row = mysql_fetch_assoc($result)) {				$dispArray[$cntArray][] = $row;				$cntArray ++;			}			// サーバ切断			$close_flag = mysql_close($link);			if ($close_flag){				//print('<p>切断に成功しました。</p>');			}	?>		<!-- ログイン処理成功時、カテゴリ表示画面にPOSTでアカウント情報を保持して遷移する -->		<form id="loginCheckForm" name="loginCheckForm" action="dispCategories.php" method="POST">			<input type="hidden" name="userID" value="<?php echo $dispArray[0][0]['id']; ?>">			<input type="hidden" name="userName" value="<?php echo $dispArray[0][0]['name']; ?>">			<input type="submit" value="カテゴリ表示画面へ">		</form>		<!-- $_SESSION['login'] = "OK";		$_SESSION['mailAddress'] = $mailAddress;		$_SESSION['password'] = $password;		// カテゴリ表示画面に遷移＠分岐画面		// include('dispCategories.php');		print '<form name = "loginCheckBranchForm" method = "POST" action = "dispBranch.php">';		print '<input type="submit" name="loginCheckBranch" value="">';		print '</form>'; -->		<?php } ?><?php } ?></body></html>